const animItems = document.querySelectorAll("._anim-items");
if (animItems.length > 0) {
    function animOnScroll() {
        for (let e = 0; e < animItems.length; e++) {
            const t = animItems[e],
                o = t.offsetHeight,
                n = offset(t).top,
                l = 4;
            let i = window.innerHeight - o / l;
            o > window.innerHeight && (i = window.innerHeight - window.innerHeight / l), pageYOffset > n - i && pageYOffset < n + o ? t.classList.add("_active") : t.classList.contains("_anim-no-hide") || t.classList.remove("_active")
        }
    }

    function offset(e) {
        const t = e.getBoundingClientRect(),
            o = window.pageXOffset || document.documentElement.scrollLeft,
            n = window.pageYOffset || document.documentElement.scrollTop;
        return { top: t.top + n, left: t.left + o }
    }
    window.addEventListener("scroll", animOnScroll), setTimeout(() => { animOnScroll() }, 300)
}
let bg = document.querySelectorAll(".mouse-parallax-bg");
if (bg.length > 0)
    for (let e = 0; e < bg.length; e++) window.addEventListener("mousemove", (function(t) {
        let o = t.clientX / window.innerWidth,
            n = t.clientY / window.innerHeight;
        bg[e].style.transform = "translate(-" + 10 * o + "px, -" + 10 * n + "px)"
    }));
let plus = document.querySelectorAll(".inner-program__plus");
plus.forEach((function(e) {
    $(e).hover((function() {
        this.parentElement.classList.remove("_tr"), this.parentElement.classList.add("_active")
    }), (function() {
        this.parentElement.classList.add("_tr"), this.parentElement.classList.remove("_active")
    }))
}));
